mapper.select:
select t1.id aid, t1.name aName, t2.id sid, t2.name sName, t3.id, t3.name from areas t1 right join schools t2 on t1.id = t2.aid right join streets t3 on t3.sid = t2.id where 1=1
and t2.aid=#{aid,jdbcType=INTEGER}
and t3.sid=#{sid,jdbcType=INTEGER}
and t3.name like concat('%',#{name,jdbcType=VARCHAR},'%')

		select t3.name 
		from areas t1 right join schools t2 on t1.id =
		t2.aid right join streets t3 on t3.sid = t2.id
		where 1=1

           and t2.aid=#{aid,jdbcType=INTEGER}  


           and t2.name like concat('%',#{name,jdbcType=VARCHAR},'%')
